import React, { useState } from "react";

import "./ExpenseForm.css";

const ExpenseForm = (props) => {

  const [formVisibility, setFormVisibility] = useState(false);
  const [enteredTitle, setEnteredTitle] = useState("");
  const [enteredAmount, setEnteredAmount] = useState("");
  const [enteredDate, setEnteredDate] = useState("");

  const addForm = () => {
  setFormVisibility(true);
  };



  // const[userInput, setUserInput] = useState({
  //   enteredTitle: '',
  //   enteredAmount: '',
  //   enteredDate: ''
  // }); Creating one state will result in handlers depending on that state, so other values would be lost
  // if we are not updating the state (which can be done using the spread operator ...userInput, or prevState)

  // const titleChangeHandler = (event) => {
  //   setEnteredTitle(event.target.value);
  //   // setUserInput((prevState) => {
  //   //   return {...prevState, enteredTitle: event.target.value}
  //   // });
  // };
  // const amountChangeHandler = (event) => {
  //   setEnteredAmount(event.target.value);
  // };
  // const dateChangeHandler = (event) => {
  //   setEnteredDate(event.target.value);
  // };

  const inputChangeHandler = (identifier, value) => {
    if (identifier === "title") {
      setEnteredTitle(value);
    } else if (identifier === "amount") {
      setEnteredAmount(value);
    } else if (identifier === "date") {
      setEnteredDate(value);
    }
  };

  const submitHandler = (event) => {
    event.preventDefault();

    const expenseData = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate),
    };

    props.onSaveExpenseData(expenseData);
    // console.log(expenseData);
    // clearing values
    setEnteredTitle('');
    setEnteredAmount('');
    setEnteredDate('');
  };

  return (
    <form onSubmit={submitHandler}>
    formVisibility ?
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input
            type="text"
            value={enteredTitle}
            onChange={(event) =>
              inputChangeHandler("title", event.target.value)
            }
          />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input
            type="number"
            value={enteredAmount}
            min="0.01"
            step="0.01"
            onChange={(event) =>
              inputChangeHandler("amount", event.target.value)
            }
          />
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input
            type="date"
            value={enteredDate}
            min="2019-01-01"
            step="2025-12-31"
            onChange={(event) => inputChangeHandler("date", event.target.value)}
          />
        </div>
      </div>
        :
        <div className="new-expense__actions">
        <button>Cancel</button>
        <button type="submit" onClick={addForm}>Add Expense</button>
      </div>
    </form>
  );
          }
export default ExpenseForm;
